import React, { useState } from "react";

import "./ExpensesFilter.css";

const ExpensesFilter = (props) => {
  const [selectedYear, setSelectedYear] = useState("");

  const year2022ClickHandler = (event) => {
    props.onFilter(event.target.value);
    console.log(event);
  };

  return (
    <div className="expenses-filter">
      <div className="expenses-filter__control">
        <label>Filter by year</label>
        <select onSelect={year2022ClickHandler}>
          <option value="2022" onClick={year2022ClickHandler}>
            2022
          </option>
          <option value="2021" onClick={year2022ClickHandler}>
            2021
          </option>
          <option value="2020" onClick={year2022ClickHandler}>
            2020
          </option>
          <option value="2019" onClick={year2022ClickHandler}>
            2019
          </option>
        </select>
      </div>
    </div>
  );
};

export default ExpensesFilter;
