import ExpenseItem from "./ExpenseItem";
import "./Expenses.css";
import Card from "../UI/Card";
import ExpensesFilter from "./ExpenseFilter/ExpensesFilter";
import React, { useState } from "react";

function Expenses(props) {
  const [yearFilter, setYearFilter] = useState("2020");

  const filterHandller = (year) => {
    console.log(year);
    setYearFilter(year);

    const expenses = props.expenses.filter(
      (expense) => expense.date.getFullYear() === yearFilter
    );

    console.log(yearFilter);
    console.log(props.expenses[0].date.getFullYear() === yearFilter);
  };

  return (
    <Card className="expenses">
      <ExpensesFilter value={yearFilter} onFilter={filterHandller} />
      {props.expenses.map((expense) => (
        <ExpenseItem
          key={expense.id}
          title={expense.title}
          amount={expense.amount}
          date={expense.date}
        />
      ))}
    </Card>
  );
}

export default Expenses;
